import React from "react";
import "../App.css";

const HeroSection = () => {

  // Smooth scroll function
  const scrollToSection = (id) => {
    const section = document.getElementById(id);
    if (section) {
      section.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <section className="hero">
      <div className="hero-content">
        <h1 className="fade-in">Transform Your Office</h1>
        <p className="fade-in delay-1">Premium interior designs for modern businesses</p>
        <div className="hero-buttons fade-in delay-2">
          <button className="btn btn-primary" onClick={() => scrollToSection("about")}>
            Get Started
          </button>
          <button className="btn btn-secondary" onClick={() => scrollToSection("projects")}>
            View Projects
          </button>
        </div>
      </div>
    </section>
  );
};

export default HeroSection;
