import React from 'react';
import heroBg from '../assets/hero-bg.jpg'; // <-- import the image

function HeroSection() {
  const handleGetStarted = () => {
    const projectsSection = document.getElementById('projects');
    projectsSection.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <section
      id="hero"
      style={{
        backgroundImage: `url(${heroBg})`, // <-- use imported image here
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        backgroundAttachment: 'fixed',
        height: '100vh',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        color: 'white',
        textAlign: 'center',
        position: 'relative'
      }}
    >
      <div className="overlay" style={{
        position: 'absolute',
        top:0, left:0,
        width:'100%',
        height:'100%',
        background:'rgba(0,0,0,0.5)',
        zIndex:1
      }}></div>

      <div className="content" style={{ zIndex:2 }}>
        <h1>Transforming Workspaces</h1>
        <p>Innovative designs for modern businesses</p>
        <button onClick={handleGetStarted}>Get Started</button>
      </div>
    </section>
  );
}

export default HeroSection;
